#limit of keys tries, from start to end
startlim = 100
endlim = 500
regex_search = True

def codebreaker(msg, startlim, endlim, regex_search):
    encmsg = str(msg)
    decodance = []
    for key in range(startlim,endlim):
        dec = encmsg[key::key+1]
        print("{} result: {}".format(key, dec))
        decodance.append(dec)
    #regex words pattern search (optional)
    if regex_search == True:
        research = []
        i=startlim-1
        for dec in decodance:
            i+=1
            found = re.findall("\w*[\s\w]*", dec)
            found[0] = str(i) + ': ' + found[0]
            research.append(found[0])

        for search in research:

            print(search)
    else: 
        pass

codebreaker(msg, startlim, endlim, regex_search = True)